import{_ as a,c as d,o as e,ah as l}from"./chunks/framework.CbuV_B65.js";const r="/assets/up-14e6a8b356b5af81ef5d29c5f11f409ec8e.MpGurWu6.webp",i="/assets/core_function.A9iUMnbA.jpg",m=JSON.parse('{"title":"Snail Job 介绍","description":"","frontmatter":{},"headers":[],"relativePath":"open-source/snail-job/index.md","filePath":"open-source/snail-job/index.md","lastUpdated":1727237826000}'),o={name:"open-source/snail-job/index.md"};function n(h,t,s,b,c,p){return e(),d("div",null,t[0]||(t[0]=[l('<h1 id="snail-job-介绍" tabindex="-1">Snail Job 介绍 <a class="header-anchor" href="#snail-job-介绍" aria-label="Permalink to “Snail Job 介绍”">​</a></h1><p>Snail Job 是一个灵活、可靠且高效的分布式任务重试和任务调度平台。它的核心采用分区模式实现，具备高度可伸缩性和容错性的分布式系统。Snail Job 提供了完善的权限管理、强大的告警监控功能和友好的界面交互，是一个高性能的分布式任务调度框架，无需外部中间件即可实现秒级任务间隔调度。</p><blockquote><p>主页预览</p></blockquote><img src="'+r+'"><blockquote><p>核心功能</p></blockquote><img src="'+i+'"><h2 id="官网" tabindex="-1">官网 <a class="header-anchor" href="#官网" aria-label="Permalink to “官网”">​</a></h2><p><a href="https://snailjob.opensnail.com/" target="_blank" rel="noreferrer">https://snailjob.opensnail.com/</a></p><h2 id="开源地址" tabindex="-1">开源地址 <a class="header-anchor" href="#开源地址" aria-label="Permalink to “开源地址”">​</a></h2><p>gitee: <a href="https://gitee.com/aizuda/snail-job" target="_blank" rel="noreferrer">https://gitee.com/aizuda/snail-job</a></p><p>github <a href="https://github.com/aizuda/snail-job" target="_blank" rel="noreferrer">https://github.com/aizuda/snail-job</a></p><h2 id="在线体验地址" tabindex="-1">在线体验地址 <a class="header-anchor" href="#在线体验地址" aria-label="Permalink to “在线体验地址”">​</a></h2><p>地址: <a href="https://preview.snailjob.opensnail.com/" target="_blank" rel="noreferrer">https://preview.snailjob.opensnail.com/</a></p><p>账号: developer</p><p>密码: 123456</p><h2 id="特性" tabindex="-1">特性 <a class="header-anchor" href="#特性" aria-label="Permalink to “特性”">​</a></h2><h3 id="易用性" tabindex="-1">易用性 <a class="header-anchor" href="#易用性" aria-label="Permalink to “易用性”">​</a></h3><ul><li>轻松接入</li><li>文档齐全</li><li>视频讲解</li></ul><h3 id="高性能" tabindex="-1">高性能 <a class="header-anchor" href="#高性能" aria-label="Permalink to “高性能”">​</a></h3><ul><li>多bucket模式</li><li>负载均衡算法</li><li>无锁化设计</li></ul><h3 id="安全性" tabindex="-1">安全性 <a class="header-anchor" href="#安全性" aria-label="Permalink to “安全性”">​</a></h3><ul><li>Token机制</li><li>访问控制</li></ul><h3 id="分布式重试任务" tabindex="-1">分布式重试任务 <a class="header-anchor" href="#分布式重试任务" aria-label="Permalink to “分布式重试任务”">​</a></h3><ul><li>数据可持久化</li><li>避免重试风暴</li><li>风险可管控</li><li>丰富的告警场景</li></ul><h3 id="分布式调度任务" tabindex="-1">分布式调度任务 <a class="header-anchor" href="#分布式调度任务" aria-label="Permalink to “分布式调度任务”">​</a></h3><ul><li>可视化任务编排</li><li>多种作业执行模式</li><li>丰富的调度方式</li><li>支持失败重试</li><li>多种阻塞策略</li><li>完善的监控报警机制</li><li>实时调度日志</li></ul><h3 id="其他特性" tabindex="-1">其他特性 <a class="header-anchor" href="#其他特性" aria-label="Permalink to “其他特性”">​</a></h3><ul><li>多样化的告警方式</li><li>数据大盘</li><li>容器化部署</li><li>用户管理</li></ul><h2 id="核心能力" tabindex="-1">核心能力 <a class="header-anchor" href="#核心能力" aria-label="Permalink to “核心能力”">​</a></h2><p>Snail Job 的核心能力包括：</p><ul><li><strong>分布式重试模块</strong>：支持本地和远程重试模式，支持多种退避策略，提供丰富的重试风暴管控手段。</li><li><strong>分布式任务调度模块</strong>：支持秒级任务调度，无需依赖外部中间件，支持多种执行模式如集群模式、广播模式和分片模式。</li></ul><h2 id="开源组件对比" tabindex="-1">开源组件对比 <a class="header-anchor" href="#开源组件对比" aria-label="Permalink to “开源组件对比”">​</a></h2><table tabindex="0"><thead><tr><th>项目</th><th>Quartz</th><th>Elastic-Job</th><th>XXL-JOB</th><th>PowerJob</th><th>Snail Job</th></tr></thead><tbody><tr><td>定时调度</td><td>Cron</td><td>Cron</td><td>Cron</td><td>CRON、固定频率、固定延迟、OpenAPI</td><td>定时任务、秒级任务、固定频率</td></tr><tr><td>重试任务</td><td>不支持</td><td>不支持</td><td>不支持</td><td>不支持</td><td>支持本地&amp;远程重试模式</td></tr><tr><td>任务编排</td><td>不支持</td><td>不支持</td><td>不支持</td><td>支持</td><td>仿钉钉工作流设计</td></tr><tr><td>分布式计算</td><td>不支持</td><td>静态分片</td><td>广播</td><td>支持</td><td>广播执行、集群执行、静态分片</td></tr><tr><td>多语言</td><td>Java</td><td>Java、脚本任务</td><td>Java、脚本任务</td><td>支持</td><td>Java(已支持)、脚本任务(开发中)</td></tr><tr><td>用户管理</td><td>不支持</td><td>支持</td><td>支持</td><td>不支持</td><td>完备的用户管理和权限管理</td></tr><tr><td>安全 Token</td><td>不支持</td><td>不支持</td><td>支持</td><td>不支持</td><td>支持</td></tr><tr><td>可视化</td><td>无</td><td>弱</td><td>历史记录、运行日志</td><td>支持</td><td>历史记录、实时日志、监控大盘</td></tr><tr><td>可运维</td><td>无</td><td>启用、禁用任务</td><td>启用、禁用任务、手动运行任务</td><td>支持</td><td>启用、禁用任务、手动运行任务</td></tr><tr><td>报警监控</td><td>无</td><td>邮件</td><td>邮件</td><td>邮件</td><td>支持配置多种告警场景</td></tr><tr><td>性能</td><td>每次调度通过DB抢锁</td><td>ZooKeeper是性能瓶颈</td><td>采用Master节点调度</td><td>无锁化设计</td><td>系统采用多bucket模式</td></tr><tr><td>接入成本</td><td>只依赖DB接入成本低</td><td>需引入Zookeeper</td><td>只依赖DB接入成本低</td><td>依赖DB接入成本低</td><td>只依赖DB接入成本低</td></tr></tbody></table><h2 id="部署" tabindex="-1">部署 <a class="header-anchor" href="#部署" aria-label="Permalink to “部署”">​</a></h2><p>Snail Job 支持多种数据库的部署，包括 MySQL、PostgreSQL、SQL Server 和 Oracle。部署步骤包括配置数据源、系统参数配置、数据库构建和启动服务。此外，Snail Job 也支持 Docker 容器部署，提供了多种数据库的 Docker 部署方案。</p><h2 id="测试案例" tabindex="-1">测试案例 <a class="header-anchor" href="#测试案例" aria-label="Permalink to “测试案例”">​</a></h2><p>Snail Job 提供了详细的测试案例，确保各个功能模块的正确性和稳定性。用户可以通过 SwaggerUI 访问和测试 Snail Job 的接口。</p><h2 id="版权" tabindex="-1">版权 <a class="header-anchor" href="#版权" aria-label="Permalink to “版权”">​</a></h2><p>Aizuda/SnailJob 采用APACHE LICENSE 2.0 开源协议，您在使用过程中，需要注意以下几点:</p><p>不得修改产品相关代码的源码头注释和出处;</p><p>不得应用于危害国家安全、荣誉和利益的行为，不能以任何形式用于非法目的;</p><h2 id="结论" tabindex="-1">结论 <a class="header-anchor" href="#结论" aria-label="Permalink to “结论”">​</a></h2><p>Snail Job 是一个高性能、高颜值、高活跃的分布式任务调度和分布式重试平台，适合需要快速部署和调整的任务，提供了丰富的功能和灵活的配置选项，满足不同用户的需求。</p>',43)]))}const f=a(o,[["render",n]]);export{m as __pageData,f as default};
